Given('another user exists with data') do   @other_user = create(:user, email: 'other@example.com')   @other_user_item = create(:item, user: @other_user) end  When('I visit my dashboard') do     visit dashboard_path end  Then('I should see my data') do   expect(page).to have_content @user.email   end  Then('I should not see the other user's data') do   expect(page).to_not have_content @other_user.email end  When('I attempt to view the other user's data') do   visit user_item_path(@other_user, @other_user_item) end  Then('I should see an unauthorized error message') do   expect(page).to have_content 'Not authorized' end  Given('I am logged in as an admin') do   @admin = create(:admin)   login_as(@admin)  end  Given('multiple users exist with data') do   @user1 = create(:user)    @item1 = create(:item, user: @user1)      @user2 = create(:user, email: 'user2@example.com')   @item2 = create(:item, user: @user2) end  When('I visit the admin dashboard') do   visit admin_dashboard_path end  Then('I should see all user data') do   expect(page).to have_content @user1.email   expect(page).to have_content @user2.email end