Given('I am logged in as an admin') do   login_as(create(:admin_user))   end  When('I view a resource') do   visit resource_path end  Then('I should have management options for that resource') do   expect(page).to have_selector '.edit-resource-button'   expect(page).to have_selector '.delete-resource-button' end  Given('I am logged in as a regular user') do   login_as(create(:user)) end  Then('I should not have management options for that resource') do   expect(page).to_not have_selector '.edit-resource-button'   expect(page).to_not have_selector '.delete-resource-button'   end  When('I access an admin page') do   visit '/admin/dashboard' end  Then('I should be able to view the admin page') do   expect(page).to have_content 'Admin Dashboard'  end  When('I attempt to access an admin page') do   visit '/admin/dashboard' end  Then('I should see an unauthorized access error message') do   expect(page).to have_content 'Unauthorized Access' end  Then('I should be redirected to my dashboard') do   expect(current_path).to eq '/dashboard' end