Given('I am logged in as an admin') do   @admin = FactoryBot.create(:user, admin: true)   login_as @admin   end  Given('I am logged in as a non-admin') do   @user = FactoryBot.create(:user, admin: false)   login_as @user end  When('I visit the page for {string}') do |title|   post = Post.find_by_title!(title)   visit post_path(post) end  Then('I should see an {string} link') do |text|   expect(page).to have_link(text) end  Then('I should not see an {string} link') do |text|   expect(page).not_to have_link(text)   end