Given('I am an existing user') do   @user = FactoryBot.create(:user)   end  When('I visit the login page') do   visit login_path end  When('I enter my valid credentials') do   fill_in 'Email', with: @user.email   fill_in 'Password', with: @user.password end  When('I enter invalid credentials') do   fill_in 'Email', with: 'invalid@example.com'    fill_in 'Password', with: 'wrongpassword' end  When('I click {string}') do |button|   click_button button end  Then('I should be logged in') do   expect(page).to have_content('Logout') end  Then('I should see my dashboard') do   expect(page).to have_content('My Dashboard')  end  Then('I should see an {string} error') do |error|   expect(page).to have_content(error) end   Then('I should not be logged in') do   expect(page).not_to have_content('Logout') end  Given('I am logged in') do   steps %(     Given I am an existing user     And I visit the login page     And I enter my valid credentials      And I click 'Login'   ) end  Then('I should be logged out') do   expect(page).not_to have_content('Logout') end  Then('I should be redirected to the login page') do   expect(page).to have_current_path(login_path)   end  Given('I am not logged in') do   # No-op   end  When('I attempt to access a protected page') do   visit protected_path end  Then('I should see an {string} message') do |message|   expect(page).to have_content(message)   end