Given('the following users exist:') do |table|   table.hashes.each do |attributes|     FactoryBot.create(:user, attributes)   end  end  Given('the following posts exist:') do |table|   table.hashes.each do |attributes|     user = User.find_by_email!(attributes.delete('user_email'))     FactoryBot.create(:post, attributes.merge(user: user))   end end  Given('I am logged in as {string}') do |email|   @user = User.find_by_email!(email)   login_as @user end  When('I visit the posts page') do   visit posts_path   end  Then('I should see {string}') do |text|   expect(page).to have_content(text) end  Then('I should not see {string}') do |text|   expect(page).not_to have_content(text)   end  When('I attempt to visit the page for {string}') do |title|   post = Post.find_by_title!(title)   visit post_path(post)  end  Then('I should see a {string} error') do |error|   expect(page).to have_content(error) end